&area; &via; &texto_via; &via_case; &limite; &lugar;
agua
shape /home/agimenez/Desktop/mapas/shapefiles/nuevos/riop
ciudad
&dbparams;
(select l.id, t.descripcion as tipo,the_geom from limite_politico l join tipo_limite t on t.id = l.tipo_id where t.descripcion = 'ciudad') as ciudad
zona
&dbparams;
(select l.id, t.descripcion as tipo,the_geom from limite_politico l join tipo_limite t on t.id = l.tipo_id where t.descripcion = 'zona') as zona
area
&dbparams;
(select a.id, t.descripcion as tipo,the_geom,a.area from area_interes a join tipo_area_interes t on t.id = a.tipo_id order by zorder,area(the_geom) desc) as area
via_case via direccion
&dbparams;
(select v.id,v.direccion,t.descripcion as tipo,the_geom from via_trazo v join tipo_via t on t.id = v.tipo_id order by zorder, prioridad, id) as via
barrio
&dbparams;
(select l.id, t.descripcion as tipo,the_geom from limite_politico l join tipo_limite t on t.id = l.tipo_id where t.descripcion = 'barrio') as barrio
texto_via
&dbparams;
(select v.id,v.abrev,t.descripcion as tipo,the_geom from via_transito v join tipo_via t on t.id = v.tipo_id order by zorder desc, prioridad, id) as texto_via
lugar texto_lugar
&dbparams;
(select p.id, t.descripcion as tipo,the_geom from punto_interes p join tipo_punto_interes t on t.id = p.tipo_id) as lugar
texto_lugar
&dbparams;
(select p.id, p.nombre, t.descripcion as tipo,the_geom from punto_interes p join tipo_punto_interes t on t.id = p.tipo_id) as lugar
texto_area
&dbparams;
(select a.id, a.nombre,t.descripcion as tipo,the_geom,a.area from area_interes a join tipo_area_interes t on t.id = a.tipo_id order by zorder,area(the_geom) desc) as texto_area
texto_limite
&dbparams;
(select l.id, nombre, t.descripcion as tipo, centroid(the_geom) as the_geom from limite_politico l join tipo_limite t on t.id = l.tipo_id) as texto_limite
